mapper.insert:
INSERT INTO hates_parts(hates_parts_no, user_id, parts_no, parts_name) SELECT seq_hates_parts_no.NEXTVAL, A.* FROM(
SELECT #{userId:VARCHAR} as user_id, #{parts.partsNo:INTEGER} as parts_no, #{parts.name:VARCHAR} as parts_name from dual
) A

mapper.update:
UPDATE users
user_name = #{userName:VARCHAR} , password = #{password:VARCHAR}, birth = #{birth:DATE} , address = #{address:VARCHAR}, phone = #{phone:VARCHAR}, email = #{email:VARCHAR}, nickname = #{nickName:VARCHAR}, profile_image = #{profileImage:VARCHAR}, profile_message = #{profileMessage:VARCHAR}, foodcategory_name1 = #{foodCategoryName1:VARCHAR}, foodcategory_name2 = #{foodCategoryName2:VARCHAR}, foodcategory_name3 = #{foodCategoryName3:VARCHAR}
WHERE user_id = #{userId}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT point_no, purchase_no, point_status, point, reg_date FROM point WHERE user_id = #{userId}
AND point_status='0'
AND point_status='1'
AND point_status='2'
ORDER BY point_no ) inner_table WHERE ROWNUM <= #{search.endRowNum} ) WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

SELECT COUNT(*) FROM( SELECT point_no, purchase_no, point_status, point, reg_date FROM point where user_id = #{userId}
AND point_status='0'
AND point_status='1'
AND point_status='2'
) countTable

